package L4_arrays;

public class print_series {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int N1=10,N2=4;
int n=1,i=0;
		
		while(i<N1)
		{
			int val=3*n+2;;
			n++;
			if(val%N2!=0)
			{
				System.out.println(val);
				i++;
			}
		
			
		
		
		}
		
		
		
	}

}
